#include "raylib.h"#include "Food.h"#include <iostream>#include <deque>#include "globalVariables.h"using namespace std;    // Food Object Constructor     Food::Food(deque<Vector2> snakeBody) {        Image image = LoadImage("greenapple.png");        ImageResize(&image, cellSize, cellSize);  // Resize food image to 30x30 pixels        texture = LoadTextureFromImage(image);        UnloadImage(image); // unload image to free memory              position = GenerateRandomCell(snakeBody); // set initial food position    }    // Texture destructor    Food::~Food() {        UnloadTexture(texture); // unload texture to free memory after food object is destroyed    }    // Draw Food on Screen Method    void Food::Draw(deque<Vector2> snakeBody) {         DrawTexture(texture, offset + position.x * cellSize, offset + position.y * cellSize, WHITE);         position = GenerateRandomCell(snakeBody);     }        // Position Randomizer    Vector2 Food::GenerateRandomCell(deque <Vector2> snakeBody) {         do {             position.x = GetRandomValue(0, cellCount - 1);             position.y = GetRandomValue(0, cellCount - 1);             return Vector2{position.x, position.y};         } while (elementInDeque(position, snakeBody));               }        