#pragma once // to indicate single inclusion of this header file during each compilation#include <deque>#include "raylib.h"using namespace std;// Snake class to manage snake properties and methodsclass Snake {public:    std::deque<Vector2> body; // Snake body represented as a deque of Vector2 positions    std::deque<Vector2> prevBody;// previous body positions for smooth movement interpolation    Vector2 direction; // direction vector to indicate movement direction, default moving right (x,y) + direction / {1,0}    bool addSegment; // boolean indicator to add segment on next update    Snake(); // Constructor    void Draw(); // Draw Snake Method    void Update(); // Update Snake Position Method    void reset(); // Reset Snake position and size Method};