#pragma once // to indicate single inclusion of this header file during each compilation#include <deque>#include "raylib.h"using namespace std;// Snake class to manage snake properties and methodsclass Snake {public:    // Snake body represented as a deque of Vector2 positions    std::deque<Vector2> body = { Vector2{6,9}, Vector2{5,9}, Vector2{4,9} };    // previous body positions for smooth movement interpolation    std::deque<Vector2> prevBody = body;    // direction vector to indicate movement direction, default moving right (x,y) + direction / {1,0}    Vector2 direction = {1, 0};    // boolean indicator to add segment on next update    bool addSegment = false;        Snake(); // Constructor        void Draw(); // Draw Snake Method    void Update(); // Update Snake Position Method    void reset(); // Reset Snake position and size Method};